import React from 'react';

export class Login extends React.Component
{
    constructor(props) {
        super(props);

        this.state =
            {
                email: '',
                password: ''
            }
    }
    handleInputChange = (event) =>
    {
        this.setState(
            {

                [event.target.name]: event.target.value
            })

    }

    submitHandler = (event) =>
    {
        const data = this.state;

        /*
        axios.post('http://172.116.201.31:8080/adduser',data)
            .then(resp => console.log(resp));
        */

        /*
        // Simple POST request with a JSON body using fetch
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ data })
        };
        fetch('http://172.116.201.31:8080/adduser', requestOptions)
            .then(response => console.log(response))
            .then(data => this.setState({ postId: data.id }));
        */
        alert(`Test Variables
               --------------
                  Email: ${this.state.email} 
               Password: ${this.state.password}
                    
                    xxxxxxxxxxxxxxxxxxxxxxxx
      
                  Email: ${data.email} 
               Password: ${data.password}
`
        );


        event.preventDefault();
    }
    render() {
        return(
            <div>
                <nav className = "navbar">
                    <a href = "" className = "navbar-brand font-italic">Second Chance</a>
                </nav>

                <div className={"container"}>
                    <div className="row">
                        <div className="col-md-2">
                               <h1 class = "text-dark display-4 mt-100" style="font-size:40px">This is a test of the log in system</h1>
                        </div>

                        <div className="col-md-4">
                            <div className="card mt-100">
                                <div className="card-body">
                                        <form className = "form" method = "post" action = "/logincheck">

                                            <label> Email</label><br />
                                            <input type="email" className = "form-control" name = "email" />
                                                <br />
                                                <br />

                                                <label> Password</label>

                                                <br />

                                                <input type = "password" className = "form-control" name = "password" />



                                        </form>
                                    <p className="mt-2"><a href="/register">Create Account</a></p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>


                <script src="https://code.jquery.com/jquery-3.3.1.slim.min.js"
                        integrity="sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo"
                        crossOrigin="anonymous"></script>
                <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js"
                        integrity="sha384-UO2eT0CpHqdSJQ6hJty5KVphtPhzWj9WO1clHTMGa3JDZwrnQq4sF86dIHNDz0W1"
                        crossOrigin="anonymous"></script>
                <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js"
                        integrity="sha384-JjSmVgyd0p3pXB1rRibZUAYoIIy6OrQ6VrjIEaFf/nJGzIxFDsf4x0xIM+B07jRM"
                        crossOrigin="anonymous"></script>
            </div>


        );
    }

}
