import React from 'react';
;
export class ClientCaseCreation extends React.Component
{
    constructor(props) {
        super(props);
        /*
        this.data = fetch('http://52.247.220.137/manage_cases?a=1').then( resp => resp.json)
            .then(result => console.log(result));
        */
        this.state = {
            pat_email: '',
            pat_notes: '',  //Details that patients can add to case
            phy_email: '',
            phy_firstName: '',
            phy_lastName: '',
            phy_bio: '',
            phy_spec: '',
            phy_hospital: '',
            pics: '',    //Images of the patient for the case
            caseTitle: ''
        };

    }

    handleInputChange = (event) =>
    {
        this.setState(
            {

                [event.target.name]: event.target.value
            })
    }

    handleSubmit = (event) =>
    {
        event.preventDefault();

        this.setState(
            {

            }
        )
    }

    render()
    {
        return(
            <div>
                <form action="/action_page.php">
                    <h1>{`${this.state.caseTitle}`}</h1>
                    <br />

                    <label>
                        <h3 style={{display:"inline"}}>Name for Case:</h3>
                        <input type={"text"} name="in_caseTitle" style={{display:"inline", width:"60%"}}
                               onClick={this.handleInputChange} />
                    </label>

                    <br />

                    <label>
                        <h3 style={{display:"inline"}}>Category:</h3>
                        <input type={"text"} name="in_category" style={{display:"inline"}}
                               onClick={this.handleInputChange} />
                    </label>

                    <br />

                    <label>
                        <h3 style={{display:"inline"}}>Select our Physician:</h3>
                        <button name="submit" style={{display:"inline"}}
                                onClick={this.handleSubmit}>Submit</button>
                    </label>
                    <label htmlFor="cars">Choose a car:</label>
                    <select name="cars" id="cars">
                        <option value="volvo">Volvo</option>
                        <option value="saab">Saab</option>
                        <option value="opel">Opel</option>
                        <option value="audi">Audi</option>
                    </select>
                </form>
            </div>
        );
    }
};
