import React from 'react';


class ClientRegister extends React.Component {
    constructor(props) {
        super(props);

        this.state =
            {
                email: '',
                password: '',
                repassword: '',
                firstName: '',
                lastName: '',
                npi: '',
                address: '',
                city: '',
                us_state: '',
                zip: '',
                picture: '',
                speciality: '',
                bio: ''
            };


    }


    handleInputChange = (event) =>
    {
        this.setState(
            {

                [event.target.name]: event.target.value
            })
    }


    handleSubmit = (event) =>
    {

        const data = this.state;
        alert(`Test Variables
               --------------
                  Email: ${this.state.email} 
               Password: ${this.state.password}
             Repassword: ${this.state.repassword}
              FirstName: ${this.state.firstName}
               LastName: ${this.state.lastName}
                    NPI: ${this.state.npi}
             Speciality: ${this.state.speciality}
                Address: ${this.state.address}
                   City: ${this.state.city}
               US_State: ${this.state.us_state}
                    Zip: ${this.state.zip}
                Picture: ${this.state.picture}
                    Bio: ${this.state.bio}
                    
                    
                    xxxxxxxxxxxxxxxxxxxxxxxx
                    
                    
                  Email: ${data.email} 
               Password: ${data.password}
             Repassword: ${data.repassword}
              FirstName: ${data.firstName}
               LastName: ${data.lastName}
                    NPI: ${data.npi}
             Speciality: ${data.speciality}
                Address: ${data.address}
                   City: ${data.city}
               US_State: ${data.us_state}
                    Zip: ${data.zip}
                Picture: ${data.picture}
                    Bio: ${data.bio}`
        );


        event.preventDefault();

    }
    render() {
        //fetch('https://api.nasa.gov/planetary/apod?api_key=DEMO_KEY').then( resp => resp.json).then(result => console.log(result));
        return (
            <form>
                <label>
                    <u>Registration for Physicians</u>
                    <br />
                </label>
                <br />
                <label>
                    Email:
                    <input
                        name="email"
                        type="text"
                        value={this.state.email}
                        onChange={this.handleInputChange}
                        required/>
                </label>



                <br />
                <label>
                    Password:
                    <input
                        name="password"
                        type="text"
                        value={this.state.password}
                        onChange={this.handleInputChange}
                        required/>
                </label>

                <br />
                <label>
                    Repeat Password:
                    <input
                        name="repassword"
                        type="text"
                        value={this.state.repassword}
                        onChange={this.handleInputChange}
                        required/>
                </label>

                <br />
                <label>
                    First Name:
                    <input
                        name="firstName"
                        type="text"
                        value={this.state.firstName}
                        onChange={this.handleInputChange}
                        required/>
                </label>

                <br />

                <label>
                    Last Name:
                    <input
                        name="lastName"
                        type="text"
                        value={this.state.lastName}
                        onChange={this.handleInputChange}
                        required/>
                </label>


                <br />
                <label>
                    Upload Profile Picture:
                    <br />
                    <input type="file" value={this.state.picture} onChange={this.handleInputChange}/>
                </label>

                <br />
                <label>
                    Bio:
                    <br />
                    <textarea  name="bio" rows="20" cols="100" value={this.state.bio} onChange={this.handleInputChange}/>
                </label>
                <br />
                <br />
                <button type="button" onClick = {this.handleSubmit}>Register</button>
            </form>
        );
    }
};

export default DrRegister;
